hour = datetime.datetime.now().hour
greeting = "Have a nice day" if hour < 20 else "Good night"
bye_msg = random.choice(
    ["See you in the next quote!", "See you tomorrow!", greeting + "!", "Until the next quote!",
     "Next quote scheduled for tomorrow!", "Look forward to the next email!", greeting + ".", "Goodbye!",
     "More quotes tomorrow", "See you later!"])

